import { Component, OnInit } from '@angular/core';
import { Formulaire } from '../shared/Models/formulaire.model';
import { FormulaireService } from '../shared/services/formulaire.service';

@Component({
  selector: 'app-template-driven',
  templateUrl: './template-driven.component.html',
  styleUrls: ['./template-driven.component.css']
})
export class TemplateDrivenComponent  {



  formulaire: Formulaire = {
    nom: '',
    email: '',
    typeCarte: '',
    numCarte: '',
    dateExp: '',
    code: ''
  };


  constructor(private formulaireService: FormulaireService) { }

  onSubmit() {
    this.formulaireService.soumettreFormulaire(this.formulaire);
  }
}
